        image: "https://images.unsplash.com/photo-1467232004584-a241de8bcf5d?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=400",
        technologies: ["React", "TypeScript", "Tailwind CSS", "Framer Motion"],
