@tailwind utilities;

:root {
  --background: hsl(248, 10%, 98%);
  --foreground: hsl(240, 10%, 9%);
  --muted: hsl(248, 10%, 95%);
  --muted-foreground: hsl(240, 5%, 45%);
  --popover: hsl(248, 10%, 98%);
  --popover-foreground: hsl(240, 10%, 9%);
  --card: hsl(248, 10%, 98%);
  --card-foreground: hsl(240, 10%, 9%);
  --border: hsl(240, 6%, 90%);
  --input: hsl(240, 6%, 90%);
  --primary: hsl(262, 83%, 58%);
  --primary-foreground: hsl(248, 19%, 97%);
  --secondary: hsl(248, 10%, 95%);
  --secondary-foreground: hsl(240, 6%, 10%);
  --accent: hsl(248, 10%, 95%);
  --accent-foreground: hsl(240, 6%, 10%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 10%, 9%);
  --radius: 0.5rem;

  /* Custom portfolio colors */
  --glass-light: hsla(248, 10%, 98%, 0.1);
  --glass-dark: hsla(240, 10%, 9%, 0.1);
  --gradient-primary: linear-gradient(135deg, hsl(262, 83%, 58%), hsl(285, 85%, 60%));
  --gradient-secondary: linear-gradient(135deg, hsl(196, 100%, 55%), hsl(170, 100%, 58%));
  --gradient-accent: linear-gradient(135deg, hsl(45, 93%, 58%), hsl(20, 90%, 58%));
}

.dark {
  --background: hsl(240, 10%, 4%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 4%, 16%);
  --muted-foreground: hsl(240, 5%, 65%);
  --popover: hsl(240, 10%, 4%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 4%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 4%, 16%);
  --input: hsl(240, 4%, 16%);
  --primary: hsl(262, 83%, 58%);
  --primary-foreground: hsl(248, 19%, 97%);
  --secondary: hsl(240, 4%, 16%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 4%, 16%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 63%, 31%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 5%, 84%);

  /* Custom portfolio colors for dark mode */
  --glass-light: hsla(0, 0%, 98%, 0.1);
  --glass-dark: hsla(240, 10%, 9%, 0.1);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer utilities {
  .glass-effect {
    @apply backdrop-blur-md bg-white/10 dark:bg-black/10 border border-white/20 dark:border-gray-700/20;
  }

  .glass-effect-strong {
    @apply backdrop-blur-xl bg-white/20 dark:bg-black/20 border border-white/30 dark:border-gray-600/30;
  }

  .gradient-text {
    @apply bg-gradient-to-r from-indigo-500 to-purple-500 bg-clip-text text-transparent;
  }

  .gradient-bg {
    background: var(--gradient-primary);
  }

  .gradient-bg-secondary {
    background: var(--gradient-secondary);
  }

  .gradient-bg-accent {
    background: var(--gradient-accent);
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse 3s infinite;
  }

  .animate-bounce-slow {
    animation: bounce 2s infinite;
  }

  .animate-gradient {
    animation: gradient 8s ease infinite;
    background-size: 400% 400%;
  }

  .animate-slide-up {
    animation: slideUp 0.6s ease-out;
  }

  .animate-slide-down {
    animation: slideDown 0.6s ease-out;
  }

  /* Horizontal scrolling enhancements */
  .horizontal-scroll {
    scrollbar-width: thin;
    scrollbar-color: rgba(156, 163, 175, 0.5) transparent;
  }

  .horizontal-scroll::-webkit-scrollbar {
    height: 8px;
  }

  .horizontal-scroll::-webkit-scrollbar-track {
    background: transparent;
    border-radius: 10px;
  }

  .horizontal-scroll::-webkit-scrollbar-thumb {
    background: rgba(156, 163, 175, 0.5);
    border-radius: 10px;
    transition: background 0.3s ease;
  }

  .horizontal-scroll::-webkit-scrollbar-thumb:hover {
    background: rgba(156, 163, 175, 0.7);
  }

  .dark .horizontal-scroll::-webkit-scrollbar-thumb {
    background: rgba(75, 85, 99, 0.5);
  }

  .dark .horizontal-scroll::-webkit-scrollbar-thumb:hover {
    background: rgba(75, 85, 99, 0.7);
  }

  /* Category header styling */
  .category-header {
    position: relative;
    display: flex;
    align-items: center;
    margin-bottom: 1.5rem;
  }

  .category-header::after {
    content: '';
    flex-grow: 1;
    height: 2px;
    margin-left: 1rem;
    background: linear-gradient(90deg, rgba(156, 163, 175, 0.3) 0%, transparent 100%);
  }

  .dark .category-header::after {
    background: linear-gradient(90deg, rgba(75, 85, 99, 0.3) 0%, transparent 100%);
  }

  .animate-fade-in {
    animation: fadeIn 0.8s ease-out;
  }

  .animate-scale-in {
    animation: scaleIn 0.5s ease-out;
  }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes gradient {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

@keyframes slideUp {
  0% { transform: translateY(100px); opacity: 0; }
  100% { transform: translateY(0); opacity: 1; }
}

@keyframes slideDown {
  0% { transform: translateY(-100px); opacity: 0; }
  100% { transform: translateY(0); opacity: 1; }
}

@keyframes fadeIn {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

@keyframes scaleIn {
  0% { transform: scale(0.9); opacity: 0; }
  100% { transform: scale(1); opacity: 1; }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  @apply bg-muted;
}

::-webkit-scrollbar-thumb {
  @apply bg-primary rounded-full;
  opacity: 0.5;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-primary;
  opacity: 0.7;
}

/* Additional glass effects */
.glass-effect {
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.glass-effect-strong {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(15px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

/* Dark mode glass effects */
.dark .glass-effect {
  background: rgba(0, 0, 0, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.05);
}

.dark .glass-effect-strong {
  background: rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

